') start_line = lines[0].strip().split(" ") method = start_line[0] url_full = start_line[1] url_split = url_full.split('?') path = url_split[0] params = url_split[1] if len(url_split) > 1 else None body_data = {} for line in lines[1:]: line = line.strip() if not line: body = "
".join(lines[lines.index(line) + 1:]) if body: parameters = body.split('&') for parameter in parameters: p, r = parameter.split('=') body_data[p] = r break return method, path, params, body_data def handle_request(self, method, url_full, headers, body_data, client_socket): # Обработка HTTP-запроса в зависимости от метода (GET или POST) if method == "GET": return self.handle_get_request(url_full, client_socket) elif method == "POST": return self.handle_post_request(url_full, body_data, client_socket) else: return "Unknown Method", 405 def handle_get_request(self, url_full, client_socket): # Обработка GET-запроса if url_full == "/": # Формируем список дисциплин и оценок для вставки в HTML grades_list = "" for value in self.data: grades_list += f"<li>{value['discipline']}: {value['grade']}</li>" # Загружаем HTML-шаблон и заменяем в нем маркер на список дисциплин и оценок with open("index.html", encoding="utf-8") as f: file = f.read() file = file.replace("GRADES", grades_list) self.send_response(client_socket, file) else: # Если URL не совпадает, возвращаем ошибку 404 with open("not_found.html", encoding="utf-8") as f: file = f.read() self.send_response(client_socket, file, status_code="404 Not Found") def handle_post_request(self, url_full, body_data, client_socket): # Обработка POST-запроса if url_full == "/add_discipline": discipline = body_data.get("discipline", "") grade = body_data.get("grade", "") try: grade = int(grade) if grade < 1 or grade > 5: raise ValueError("Grade must be between 1 and 5") except ValueError: # Если оценка некорректна, возвращаем ошибку 400 self.send_response(client_socket, "Invalid grade. Grade must be a number between 1 and 5", status_code="400 Bad Request") return # Добавляем данные о дисциплине и оценке в список self.data.append({"discipline": discipline, "grade": grade}) # Формируем HTML для отображения всех дисциплин и оценок grades_list = "" for item in self.data: grades_list += f"<tr><td>{item['discipline']}</td><td>{item['grade']}</td></tr>" with open("index.html", encoding="utf-8") as f: html_file = f.read() html_file = html_file.replace("<!-- display all disciplines and grades -->", grades_list) self.send_response(client_socket, html_file) else: # Если URL не совпадает, возвращаем ошибку 404 with open("not_found.html", encoding="utf-8") as f: html_file = f.read() self.send_response(client_socket, html_file, status_code="404 Not Found") def send_response(self, client_socket, response=None, status_code="200 OK"): # Отправка HTTP-ответа клиенту if response is None: response = "Internal Server Error" response_headers = { "Content-Type": "text/html; charset=utf-8", "Connection": "close", } response_headers_raw = "".join( f"{k}: {v}
